import './chat.css'
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faPaperPlane } from '@fortawesome/free-solid-svg-icons'
import { useEffect, useState } from 'react';

export default function Chat() {

    const [user, setUser] = useState("");

    useEffect(() => {
        //carrega o nome do usuário enviado anteriormente
        handlerEnter();
    }, []);

    function handlerClick() {
        const mensagens = document.querySelector('.mensagens');
        const mensagem = document.querySelector('#mensagem');
        mensagens.innerHTML += `
        <fieldset>
            <legend>${user || "Desconhecido"}</legend>
            <p>${mensagem.value}</p>
        </fieldset>
        `
        mensagem.value = "";
    }

    function handlerEnter(){
        const mensagem = document.querySelector('#mensagem');
    }

    return (
        <div id='container'>
            <div className='mensagens'>
                <fieldset>
                    <legend>Arthur Fakhouri</legend>
                    <p>Mensagem</p>
                </fieldset>
            </div>
            <div className='controls'>
                <input type="text" placeholder='Digite sua mensagem' id="mensagem"/>
                <button onClick={handlerClick}><FontAwesomeIcon icon={faPaperPlane} /></button>
            </div>
        </div>
    )
}
