import './chat.css'
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faPaperPlane } from '@fortawesome/free-solid-svg-icons'
import { useContext, useEffect } from 'react';
import { UserContext } from '../../contexts/user';
import { useNavigate } from 'react-router-dom';
import io from 'socket.io-client';

const socket = io.connect('http://localhost:3001');

export default function Chat() {
    const { user } = useContext(UserContext);
    const navigate = useNavigate();

    useEffect(() => {
        if (!user) {
            navigate('/');
        }
        socket.emit('user_connected', { user: user });
        socket.on('msgToUser', (data) => {
            if(data.isServer)
                textServer(data.text);
        });
        handlerEnter();
    }, []);

    function textServer(text) {
        const mensagens = document.querySelector('.mensagens');
        const mensagem = document.querySelector('#mensagem');
        mensagens.innerHTML += `
        <p class='serverText'>${text}</p>
        `
        mensagem.value = "";
        mensagem.scrollTop = mensagens.scrollHeight;
    }

    function handlerClick() {
        const mensagens = document.querySelector('.mensagens');
        const mensagem = document.querySelector('#mensagem');
        if (mensagem.value.trim()) {
            mensagens.innerHTML += `
        <fieldset>
            <legend>${user}</legend>
            <p>${mensagem.value}</p>
        </fieldset>
        `
            mensagem.value = "";
            mensagens.scrollTop = mensagens.scrollHeight;
            socket.emit('msgToUsers')
        }
    }

    function handlerEnter() {
        const msg = document.querySelector('#mensagem');
        msg.addEventListener("keydown", (e) => {
            if (e.key === "Enter")
                handlerClick();
        });
    }

    return (
        <div id='container'>
            <div className='mensagens'>
            </div>
            <div className='controls'>
                <input type="text" placeholder='Digite sua mensagem' id='mensagem' />
                <button onClick={handlerClick}><FontAwesomeIcon icon={faPaperPlane} /></button>
            </div>
        </div>
    )
}
