import './chat.css'
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faPaperPlane } from '@fortawesome/free-solid-svg-icons'
import { useContext, useEffect } from 'react';
import { UserContext } from '../../contexts/user';
import { useNavigate } from 'react-router-dom';
import io from 'socket.io-client';

const socket = io.connect('http://localhost:3001');

export default function Chat() {
    const { user } = useContext(UserContext);
    const navigate = useNavigate();

    useEffect(() => {
        if (!user) {
            navigate('/');
        }
        socket.emit('user_connected', { user: user, isServer: true });
        handlerEnter();
    }, []);

    function loadTexts() {
        socket.on('msgFromServer', (data) => {
            textServer(data.text);
        });
        socket.on('msgToUser', (data)=>{
            textUser(data.user, data.text, data.cur);
        });
    }

    function textServer(text) {
        const mensagens = document.querySelector('.mensagens');
        mensagens.innerHTML += `
        <p class='serverText'>${text}</p>
        `
        mensagens.scrollTop = mensagens.scrollHeight;
    }

    function textUser(user, text, cur) {
        const mensagens = document.querySelector('.mensagens');
        const mensagem = document.querySelector('#mensagem');
        let style = '';
        if(cur)
            text = 'margin-left: auto;margin-right: 0px';
        document.querySelector('#mensagem');
        mensagens.innerHTML += `
        <fieldset ${text}>
        <legend>${user}</legend>
        <p>${text}</p>
        </fieldset>
        `
        mensagem.value = "";
        mensagens.scrollTop = mensagens.scrollHeight;
    }

    function handlerClick() {
        const mensagem = document.querySelector('#mensagem');
        socket.emit('msgFromUser', {user: user, text: mensagem.value});
    }

    function handlerEnter() {
        const msg = document.querySelector('#mensagem');
        msg.addEventListener("keydown", (e) => {
            if (e.key === "Enter")
                handlerClick();
        });
    }

    return (
        <div id='container'>
            <div className='mensagens'>
            </div>
            <div className='controls'>
                <input type="text" placeholder='Digite sua mensagem' id='mensagem' />
                <button onClick={handlerClick}><FontAwesomeIcon icon={faPaperPlane} /></button>
            </div>
        </div>
    )
}
